void lcd_buttons_init(){
	//开始采音按钮
	lcd_draw_circle(70,155,40,0xB686);
	lcd_draw_circle(70,155,35,0x45CF);
	for(int i=-20;i<=20;i++){
		int length=(i+20)/2+1;
		lcd_draw_line(70-length,150+i,70+length,150+i,0x7DC1);
	}
	//暂停采音按钮
	lcd_draw_circle(70,60,40,0xFD62);			
	lcd_draw_circle(70,60,35,0xE423);
	lcd_draw_rectangle(50,45,90,55,YELLOW);
	lcd_draw_rectangle(50,65,90,75,YELLOW);
	//终止采音按钮
	lcd_draw_circle(175,155,40,RED);			
	lcd_draw_circle(175,155,35,0xA189);
	lcd_draw_rectangle(160,140,190,170,0xA902);
}

int y_start[21],y_end[21];							//不同频率对应的y轴坐标

int bar_heights[21];								//频率条高度
void lcd_spectrum_init(){
	for(int i=0;i<21;i++){
		y_start[i]=475-7*i+1;
		y_end[i]=475-7*i+6;
		bar_heights[i]=5;
		lcd_draw_rectangle(200,y_start[i],205,y_end[i],WHITE);
	}
}

void lcd_spectrum_set_height(int index,int height){
	int origin_height=bar_heights[index];
	if(origin_height<=5 && height<=5) return;
	if(height<=5) height=5;
	if(height>=120) height=120;
	lcd_draw_rectangle( 205-origin_height,y_start[index],
						205-height,y_end[index],
						origin_height>height?BLACK:WHITE );
	bar_heights[index]=height;
}

