void lcd_scale_debug(){
	for(int x=10;x<=480;x+=10)								//竖线标尺
		if(x%50==0) lcd_draw_line(x,0,x,800,LIGHTBLUE);		//高亮
		else lcd_draw_line(x,0,x,800,DARKBLUE);
	for(int y=10;y<=790;y+=10)								//横线标尺
		if(y%50==0) lcd_draw_line(0,y,480,y,LIGHTBLUE);		//高亮
		else lcd_draw_line(0,y,480,y,DARKBLUE);
}

