void lcd_stave_note(int pitch){
	lcd_note_press(pitch,1);
	if(pitch<13)
		stave[pitch][0]=1;
}

int pitch_color[13]={0xcfe0,0xbfe2,0xafe5,0x9fc8,0x8fca,0x7fad,0x6fb0,0x57b2,0x4795,0x3798,0x277a,0x177d,0x077f};
void lcd_stave_line(int pitch,int index,int litup){
	int start=x_start[pitch];
	int end=x_end[pitch];
	int y=301+index*2;
	int color=pitch_color[pitch];
	if(litup){
		lcd_draw_line(start,y,end,y,color);
		lcd_draw_line(start,y+1,end,y+1,color);
	}else{
		lcd_draw_line(start,y,end,y,BLACK);
		lcd_draw_line(start,y+1,end,y+1,BLACK);
		if(pitch==2 || pitch==4 || pitch==6 || pitch==8 || pitch==10){
			lcd_draw_point((start+end)/2,y,WHITE);
			lcd_draw_point((start+end)/2,y+1,WHITE);
		}
	}
	stave_colored[pitch][index]=litup;
}

void lcd_stave_update(){
	for(int pitch=0;pitch<13;pitch++){
		int state=stave[pitch][0];
		for(int i=1;i<75;i++){
			int tmp=stave[pitch][i];
			stave[pitch][i]=state;
			state=tmp;
		}
		stave[pitch][0]=0;
		
		for(int i=1;i<75;i++){
			if(stave[pitch][i] && !stave_colored[pitch][i])
				lcd_stave_line(pitch,i,1);
			if(!stave[pitch][i] && stave_colored[pitch][i])
				lcd_stave_line(pitch,i,0);
		}
	}
}

int note_pos[7];
void lcd_note_init(){
	lcd_draw_rectangle(290,655,350,760,WHITE);
	lcd_draw_rectangle(380,655,440,760,WHITE);
	for(int i=0;i<8;i++){
		int y=655+i*15;
		lcd_draw_rectangle(290,y,350,y+1,BLACK);
		lcd_draw_rectangle(380,y,440,y+1,BLACK);
		if(i==1 || i==2 || i==3 || i==5 || i==6){
			lcd_draw_rectangle(290,y-5,325,y+6,BLACK);
			lcd_draw_rectangle(380,y-5,415,y+6,BLACK);
		}
		if(i<7) note_pos[6-i]=y+14;
	}
}

int sta=0;

void lcd_note_press(int pitch,int press){
	if(!sta) return;
	int x=326,y=note_pos[pitch%7];
	if(pitch>=7) x+=90;
	lcd_draw_rectangle(x+9,y-3,x+15,y-9,press?DARKBLUE:WHITE);
}

void clear(){
	for(int pitch=0;pitch<13;pitch++){
		for(int index=1;index<75;index++){
			lcd_stave_line(pitch,index,0);
			stave[pitch][index]=0;
			stave_colored[pitch][index]=0;
		}
		lcd_note_press(pitch,0);
	}
	lcd_note_press(13,0);
} 

void start(){
	sta=1;
}
void stop(){
	sta=0;
}
